<script>
    // Format nilai gaji saat halaman dimuat
    formatSalary();

    // Format nilai gaji saat nilai berubah
    document.getElementById('floating_salary').addEventListener('input', formatSalary);

    function formatSalary() {
        // Ambil nilai gaji dari input
        let salary = document.getElementById('floating_salary').value;
        // Hapus karakter yang bukan angka
        salary = salary.replace(/\D/g, '');
        // Format nilai gaji dengan titik setiap tiga digit
        salary = salary.replace(/\B(?=(\d{3})+(?!\d))/g, ".");
        // Tampilkan nilai gaji di input
        document.getElementById('floating_salary').value = salary;
    }
</script>

